				["finish"] = {
				},
				["init"] = {
					["custom"] = "-- Buds#0500 @ Discord  - https://wago.io/p/Buds\n-- https://wago.io/H1DUIp0zm\n-- update: 06/09/2018\n\n--  heck healing done by other healers in group (set false to improve fps)\naura_env.check_incoming_heal = true\n\n-- add delay to smite cast time\naura_env.reaction_time = 500\n\n-- ignore units with more than 99% HP (lower to improve fps)\naura_env.health_deficit_threshold = 99\n\n-- don't use smite if 50% of units with atonement have more than 99% HP\naura_env.min_threshold_pct = 50\n\n--green if total healing from a smite heal is >= 100% of a single atonement smite heal (might have to change how this is calculated)\naura_env.color = {\n    [\"green\"] = 100.0,\n    [\"yellow\"] = 70.0,\n    [\"orange\"] = 40.0\n}\n\n--------------------\naura_env.healers = {}\n\n\naura_env.atonement_count = function()\n    local triggerInfo = WeakAuras.triggerState[aura_env.id][1][\"\"]\n    return triggerInfo and triggerInfo.stacks or 0\nend\naura_env.atonement_list = function()\n    local triggerInfo = WeakAuras.triggerState[aura_env.id][1][\"\"]\n    return triggerInfo and triggerInfo.name and string.gmatch(triggerInfo.name, \"%S+\")\nend\n\n\naura_env.sotm = {\n    [0] = 1.12,\n    [1] = 1.12,\n    [2] = 1.10,\n    [3] = 1.09,\n    [4] = 1.08,\n    [5] = 1.07,\n    [6] = 1.06,\n    [7] = 1.05,\n    [8] = 1.04\n}\nsetmetatable(aura_env.sotm, {__index = function () return 1.03 end}) -- default 1.03\n\naura_env.makehealerlist = function()\n    aura_env.healers = {}\n    for groupmember in WA_IterateGroupMembers() do\n        if UnitGroupRolesAssigned(groupmember) == \"HEALER\" and UnitGUID(groupmember) ~= WeakAuras.myGUID then\n            table.insert(aura_env.healers, groupmember)\n        end\n    end\nend\n\n\nfunction aura_env.calc_healing_from_spell(cast_time, healing_per_unit)\n    local now = GetTime()\n    local heal_hit, heal_crit = 0, 0\n    \n    local count_atonement_filtered = 0 -- count units with atonement up when cast finish\n    local count_atonement_filtered_underthreshold = 0 -- count units with atonement up when cast finish + hp < threshold\n    \n    -- get atonement list from aura trigger, save a lot of time\n    local ato_list = aura_env.atonement_list()\n    if ato_list then\n        for unit in ato_list do\n            local ato = select(6, WA_GetUnitBuff(unit, 194384, \"PLAYER\"))\n            local atonement_remaining_time = ato and ato - now or 0\n            -- filter if buff drop before end of cast\n            if atonement_remaining_time*1000 > cast_time + aura_env.reaction_time then\n                count_atonement_filtered = count_atonement_filtered +1\n                \n                local hp_max, hp_cur = UnitHealthMax(unit), UnitHealth(unit)\n                local health_deficit = hp_max - hp_cur\n                local hp_pct = hp_cur / hp_max * 100\n                -- check deficit threshold\n                if hp_pct < aura_env.health_deficit_threshold then\n                    count_atonement_filtered_underthreshold = count_atonement_filtered_underthreshold + 1\n                    -- incoming heal\n                    if aura_env.check_incoming_heal then\n                        if UnitGetIncomingHeals(unit) > 0 then\n                            -- incoming heal source(s)\n                            local external_healing_total = 0\n                            for _,healer in pairs(aura_env.healers) do\n                                local external_healing = UnitGetIncomingHeals(unit, healer) or 0\n                                if external_healing > 0 then\n                                    -- process only incoming heals that will land before the smite\n                                    local when_lands = select(5, UnitCastingInfo(healer))\n                                    if when_lands then\n                                        when_lands = when_lands - now*1000\n                                        if when_lands < cast_time + aura_env.reaction_time then\n                                            external_healing_total = external_healing_total + external_healing\n                                        end\n                                    end\n                                end\n                            end\n                            external_healing_total = external_healing_total < health_deficit and external_healing_total or health_deficit\n                            health_deficit = health_deficit - external_healing_total\n                        end\n                    end\n                    -- compute the heal received by this unit\n                    local this_hit = health_deficit < healing_per_unit and health_deficit or healing_per_unit\n                    local this_crit = health_deficit < healing_per_unit*2 and health_deficit or healing_per_unit*2\n                    heal_hit = heal_hit + this_hit\n                    heal_crit = heal_crit + this_crit\n                end\n            end\n        end\n    end\n    \n    if count_atonement_filtered > 0 then\n        local count_atonement_filtered_underthreshold_pct = count_atonement_filtered_underthreshold / count_atonement_filtered * 100\n        return heal_hit, heal_crit, count_atonement_filtered_underthreshold_pct\n    else\n        return 0, 0 ,0\n    end\nend",
