		["DK-Blood Death Strike v2.3"] = {
			["outline"] = true,
			["text2Point"] = "CENTER",
			["text1FontSize"] = 18,
			["xOffset"] = -27.9998779296875,
			["displayText"] = "%c",
			["customText"] = "function()\n    local i = 1\n    local cur = GetTime()\n    local total = 0\n    \n    --get total damage taken\n    while aura_env.table[i] do\n        --grab table entries with time and damage\n        local time = aura_env.table[i][1]\n        local dmg = aura_env.table[i][2]\n        \n        --if the damage happened outside of the timewindow\n        if cur > time + aura_env.lastSec then\n            --remove the entry\n            table.remove(aura_env.table, i)\n        else --otherwise\n            --add up the damage and go to next entry\n            total = total + dmg\n            i = i + 1\n        end\n    end\n    \n    --Vampiric Blood\n    --check artifact traits\n    local currentRank = 0\n    local loaded = IsAddOnLoaded(\"LibArtifactData-1.0\") or LoadAddOn(\"LibArtifactData-1.0\")\n    if loaded then\n        aura_env.LAD = aura_env.LAD or LibStub(\"LibArtifactData-1.0\")\n        if not aura_env.LAD:GetActiveArtifactID() then\n            aura_env.LAD:ForceUpdate()            \n        end\n        local _, traits = aura_env.LAD:GetArtifactTraits()\n        if traits then\n            for _,v in ipairs(traits) do\n                if v.spellID == 192544 then\n                    currentRank = v.currentRank\n                    break\n                end\n            end\n        end\n    end\n    local trait = 1.3 + 0.05 * currentRank\n    local vamp = UnitBuff(\"player\", GetSpellInfo(55233)) and trait or 1\n    \n    --Versatility\n    local vers = 1 + ((GetCombatRatingBonus(29) + GetVersatilityBonus(30)) / 100)\n    \n    --Leech\n    local leech = 1 + GetLifesteal() / 100\n    \n    --Lana'thel's Lament legendary\n    local leg = UnitBuff(\"player\", GetSpellInfo(188290)) and IsEquippedItem(133974) and 1.05 or 1\n    \n    --Guardian Spirit\n    local gs = UnitBuff(\"player\", GetSpellInfo(47788)) and 1.4 or 1\n    \n    --Divine Hymn\n    local dh = UnitBuff(\"player\", GetSpellInfo(64844)) and 1.1 or 1\n    \n    \n    local heal = total * aura_env.percDmg --damage taken * DS percentage\n    local perc = heal / UnitHealthMax(\"player\") --relative to maxHP\n    perc = math.max(aura_env.minHealthPerc, perc) --minimum DS percentage\n    perc = perc * vamp * vers * leech * gs * dh * leg --apply all multipliers\n    heal = perc * UnitHealthMax(\"player\") --get the actual heal value\n    \n    if aura_env.showAbsolute then\n        return aura_env.shortenNumber(heal)\n    end\n    return string.format(\"%.1f%%%%\", perc*100)\nend",
