stTime, edTime)\n    local adding_cast_eng = 0\n    local NewPosition = UnitPower('player',aura_env.my_energy_type)+add_eng;\n    local CurPosition = UnitPower('player',aura_env.my_energy_type);\n    local MaxPosition = UnitPowerMax(\"player\", aura_env.my_energy_type);\n    \n    if stTime\n    then\n        adding_cast_eng = add_eng*(GetTime()*1000 -stTime)/(edTime-stTime)\n    end\n    \n    if aura_env.check_ping and stTime\n    then\n        aura_env.saveEpositionOnPing = NewPosition\n        aura_env.saveTpositionOnPing = GetTime()\n    elseif aura_env.myApprPing+aura_env.saveTpositionOnPing > GetTime()\n    then\n        NewPosition = aura_env.saveEpositionOnPing\n    end\n    \n    \n    if NewPosition < CurPosition\n    then\n        return math.max(NewPosition, 0), CurPosition, MaxPosition, CurPosition+adding_cast_eng\n    else\n        return CurPosition, math.min(NewPosition, MaxPosition), MaxPosition, CurPosition+adding_cast_eng\n    end\nend",
