						["custom"] = "function(a, event, ...)\n    local e = aura_env\n    \n    --[[if event == \"FRAME_UPDATE\" then\n        local time = GetTime()\n        \n        e.last = e.last or time\n        \n        if e.last >= time - 0.5 then\n            return true\n        end\n        \n        e.last = time\n    end]]--\n    \n    --if event == \"UNIT_AURA\" and (...) ~= \"player\" then return true end\n    \n    if event == \"PLAYER_SPECIALIZATION_CHANGED\" \n    or event == \"PLAYER_REGEN_DISABLED\" \n    or event == \"PLAYER_TALENT_UPDATE\" then\n        e.UpdateKnownSpells()\n        e.maxAuras = #e.KnownSpells\n    end\n    \n    for _, v in ipairs(a) do\n        v.show = false\n        v.changed = true\n    end\n    \n    local GetSpellTexture = GetSpellTexture\n    \n    if e.maxAuras > 0 then\n        for i = 1, e.maxAuras do\n            a[i] = a[i] or {}\n            \n            local a = a[i]\n            local spell = e.KnownSpells[i]\n            local id = spell.id\n            local stacks, dur, exp, max, pand, icon, type = 0, 0, 0, 0, 0, 0, \"\"\n            \n            if e.SpecializedSpells[id] then\n                stacks, dur, exp, max, pand, icon, type = e.SpecializedSpells[id]()\n            else\n                if spell.filters.HELPFUL then\n                    stacks, dur, exp = e.AuraInfo(spell.applies, \"HELPFUL\")\n                    icon = GetSpellTexture(spell.applies)\n                    type = \"HELPFUL\"\n                end\n                \n                if spell.filters.HARMFUL then\n                    stacks, dur, exp = e.AuraInfo(spell.applies, \"HARMFUL\")\n                    icon = GetSpellTexture(spell.applies)\n                    type = \"HARMFUL\"\n                end\n                \n                if spell.filters.COOLDOWN and dur == 0 then\n                    local start\n                    \n                    type, start, dur = \"COOLDOWN\", GetSpellCooldown(id)\n                    exp = dur > 1.5 and start + dur or 0\n                    dur = dur > 1.5 and dur or 0\n                    icon = GetSpellTexture(id)\n                end\n                \n                if spell.filters.CHARGE and dur == 0 then\n                    local start\n                    \n                    type, stacks, max, start, dur = \"CHARGE\", GetSpellCharges(id)\n                    exp = stacks ~= max and dur > 1.5 and start + dur or 0\n                    dur = stacks ~= max and dur > 1.5 and dur or 0\n                    icon = GetSpellTexture(id)\n                end\n                \n                pand = spell.pand or e.timerParameters[type].t\n            end\n            \n            a.show = true\n            a.changed = true\n            a.autoHide = false\n            a.progressType = \"timed\"\n            a.stacks = stacks\n            a.duration = dur\n            a.expirationTime = exp\n            a.icon = icon\n            a.index = i\n            \n            a.spellID = id\n            a.name = (GetSpellInfo(id))\n            a.type = type\n            a.shift = pand\n            a.maxCharges = max\n            a.debuffNotApplied = type == \"HARMFUL\" and exp == 0\n            a.inPandemic = type == \"HARMFUL\" and exp > 0 and exp - GetTime() <= pand\n            a.isReady = (type == \"COOLDOWN\" or type == \"CHARGE\") and exp == 0\n        end\n    end\n    \n    return true\nend\n\n",
