env.orbs = 0\naura_env.icon = enableIcon\naura_env.marks = enableMarks\naura_env.npcID = { -- possible to add more \n    [120651] = true, -- fel explosives\n    --[92168] = true, -- testingdummies\n    --[92166] = true, -- testingdummies\n}\naura_env.marksBlocked = false",
