				["init"] = {
					["custom"] = "aura_env.GetPandemic = function()\n    local e = aura_env\n    local s = aura_env.state\n    local round = function(num)\n        num = math.floor( (num*10)+0.5 )/10\n        return num\n    end\n    -- if details are equal then this is still the same application\n    if e[s.GUID] and s.expirationTime == e[s.GUID].expires then\n        -- ...so just return what we already have\n        aura_env.state.Pan = e[s.GUID].pandemic\n        return e[s.GUID].pandemic\n    end \n    if not e[s.GUID] then\n        -- it is the first time through.\n        e[s.GUID] = {}\n        e[s.GUID].duration = s.duration\n        e[s.GUID].pandemic = round(s.duration*0.3)\n    elseif s.expirationTime - s.duration > e[s.GUID].expires then\n        -- this is a recast\n        e[s.GUID].duration = s.duration\n        e[s.GUID].pandemic = round(s.duration*0.3)\n    else\n        local refresh = s.duration - (e[s.GUID].duration + e[s.GUID].pandemic)\n        -- if this refresh is abnormal\n        if refresh < 0 - e[s.GUID].pandemic\n        or refresh > 0.2\n        then\n            --Exceptions!!\n            local cost = GetSpellPowerCost(s.spellId)\n            if (cost[2] and cost[2].name == \"COMBO_POINTS\") --Is this a CP ability?\n            or s.spellId == 188389 -- or is it Flame Shock\n            then \n                --calc how much pandemic was added\n                local difference = e[s.GUID].expires - (s.expirationTime - s.duration)\n                e[s.GUID].duration = s.duration - math.min(difference, (s.duration/1.3)*0.3)\n                e[s.GUID].pandemic = round(e[s.GUID].duration * 0.3)\n            end\n        end\n    end\n    -- Catch-all\n    -- \"normal\" refreshes will fall through to here \n    -- ...as will any abnormal ones that we let fall through \n    e[s.GUID].expires = s.expirationTime\n    s.Pan = e[s.GUID].pandemic\n    return e[s.GUID].pandemic\nend",
