             spell = GetSpellInfo(221603)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if getRange(unit) >= 10 then\n                        color=aura_env.YELLOW\n                    end\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M, duration)\n                end\n                \n                -- Eye of Gul'dan 1\n                spell = GetSpellInfo(209454)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if getRange(unit) >= 10 then\n                        color={0.53,0.8,0.92}\n                    end\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M)    \n                end\n                \n                -- Eye of Gul'dan 2\n                spell = GetSpellInfo(221728)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if getRange(unit) >= 10 then\n                        color={0.53,0.8,0.92}\n                    end\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M)    \n                end\n                \n                local _, _, difficulty = GetInstanceInfo()\n                if difficulty == 16 then\n                    -- Parasitic Wound\n                    spell = GetSpellInfo(206847)\n                    if UnitDebuff(unit, spell) then\n                        local color = {1,0,0}\n                        if getRange(unit) >= 8 then color = {1,0.9,0} end\n                        local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                        duration = aura_env.round(expires - GetTime(), 0)\n                     