						["custom"] = "function(event,unit)\n    if aura_env.Force_No_FriendlyBars then\n        local frame = C_NamePlate.GetNamePlateForUnit(unit);\n        if UnitCanAttack(frame.UnitFrame.unit, \"player\") then\n            frame.UnitFrame.healthBar:Show()\n        else \n            frame.UnitFrame.healthBar:Hide()\n        end\n    end\nend",
