		["Warlock: PvP Talents"] = {
			["text2Point"] = "CENTER",
			["text1FontSize"] = 14,
			["cooldownTextEnabled"] = false,
			["yOffset"] = -325,
			["anchorPoint"] = "CENTER",
			["customTextUpdate"] = "update",
			["url"] = "https://wago.io/Vyx12GkVZ/32",
			["actions"] = {
				["start"] = {
					["do_message"] = false,
					["custom"] = "local r = aura_env.region\nr.cooldown:SetDrawEdge(true)\nr.stacks:ClearAllPoints()\nr.stacks:SetPoint(\"RIGHT\", r, \"BOTTOM\", 0.45*r.width, 0.075*r.height)",
					["do_custom"] = false,
				},
				["init"] = {
					["custom"] = "--------------------------------------------\n--           MULTISPELL TRACKER           --\n--         Made by Kirihn-Area 52         --\n-- \"The headless pigmy has terrible aim.\" --\n--       Some additions by Not-Illidan :) --\n--------------------------------------------\n\n--[[ What 'timerParameters' is for:\n\nThis table indicates the time (in seconds) when the timer will shift in color,\n    and the colors used for text, depending on the filter used in 'talents',\n    following this structure:\n\n    timerParameters = {\n        [filter] = {\n            t = ShiftTime,\n            rN = redNormal, gR = greenNormal, bR = blueNormal,\n            rS = redShift, gS = greenShift, bS = blueShift\n        }\n    }\n\nColor values must be between 0 and 1. It's convenient to use value/255.\n\nFor complex color handling, use the 'SpecializedColoring' table.\n\n]]--\n\naura_env.timerParameters = {\n    [\"COOLDOWN\"] = {\n        t = 15,\n        rN = 1, gN = 150/255, bN = 0,\n        rS = 1, gS = 1, bS = 0\n    },\n    [\"CHARGE\"] = {\n        t = 3,\n        rN = 1, gN = 150/255, bN = 0,\n        rS = 1, gS = 1, bS = 0\n    },\n    [\"HARMFUL\"] = {\n        t = 6,\n        rN = 1, gN = 1, bN = 0,\n        rS = 1, gS = 50/255, bS = 0\n    },\n    [\"HELPFUL\"] = {\n        t = 6,\n        rN = 0, gN = 1, bN = 0,\n        rS = 1, gS = 150/255, bS = 0\n    }\n}\n\n--[[ 'talents' Table Structure:\n\ntalents = {\n    [orderPriority] = {\n        {id = spellID, filters = {filtersTable}[, applies = appliesSpellID][, pand = pandemicTimer][, condition = spellID]}\n    }\n}\n\nFilters:\n    \"COOLDOWN\" -> Cooldown spell, without charges\n    \"CHARGE\" -> Spell with charges\n    \"HARMFUL\" -> Debuff, requires 'appliesSpellID'\n    \"HELPFUL\" -> Buff, requires 'appliesSpellID'\n\nAdd an aura filter after \"COOLDOWN\" or \"CHARGE\" for CDs that apply an aura (if you wish\n    to track it), e.g., filter = {\"COOLDOWN\", \"HELPFUL\"}\n\nThe optional 'pand' key will overwrite the 't' value from 'timerParameters'.\n\nThe optional 'condition' key serves as a spell that must be known for the\n    display to be built.\n\nAllow each 'orderPriority' key to only contain mutually exclusive spells.\n\nWriting down 'orderPriority' keys is technically unecessary, but it's easier \n    to read that way.\n\nFor complex spell handling (duration, stacks etc), use the 'SpecializedSpells' table.\n\n]]--\n\nlocal talents = {--I like doing Dots/Buffs -> CDs -> CD Buffs\n    [1] = {--Medallion Options\n        {id = 208683, filters = {\"COOLDOWN\"}}, --Gladiator's Medallion\n        {id = 214027, filters = {\"COOLDOWN\"}}, --Adaptation\n    },\n    \n    [2] = {--Curses Caster\n        {id = 199890, filters = {\"COOLDOWN\", \"HARMFUL\"}, applies = 199890} --Curse of Tongues\n    },\n    \n    [3] = {--Curses Melee\n        {id = 199892, filters = {\"COOLDOWN\", \"HARMFUL\"}, applies = 199892} --Curse of Weakness\n    },\n    \n    [4] = {--Curses Health\n        {id = 199954, filters = {\"COOLDOWN\", \"HARMFUL\"}, applies = 199954} --Curse of Fragility\n    },\n    \n    [5] = {--Curses Dmg Amp\n        {id = 234877, filters = {\"COOLDOWN\", \"HARMFUL\"}, applies = 234877} --Curse of Shadows\n    },\n    \n    [6] = {--Soul Shatter\n        {id = 212356, filters = {\"COOLDOWN\", \"HELPFUL\"}, applies = 236471} --Soul Shatter\n    },\n    \n    [7] = {--Casting Circle\n        {id = 221703, filters = {\"COOLDOWN\", \"HELPFUL\"}, applies = 221705} --Casting Circle\n    },\n    \n    [8] = {--Call Fel Lord\n        {id = 212459, filters = {\"COOLDOWN\"}} --Call Fel Lord\n    },    \n    \n    [9] = {--Call Observer\n        {id = 201996, filters = {\"COOLDOWN\"}} --Call Observer\n    },\n    \n    [10] = {--Nether Ward\n        {id = 212295, filters = {\"COOLDOWN\", \"HELPFUL\"}, applies = 212295} --Nether Ward\n    },\n    \n}\n\n\n--[[ What SpecializedColoring is for:\n\nThis table stores functions with special instructions for how the coloring of\n    a spell should be handled.\n\nStructure:\n    \n    SpecializedColoring = {\n        [spellID] = function() --body end,\n    }\n\n]]--\n\naura_env.SpecializedColoring = {\n    [196412] = --Eradication Assist\n    function()\n        local s, r = aura_env.state, aura_env.region\n        local exp, dur = s.expirationTime, s.duration\n        local remain = dur > 0 and exp - GetTime() or 0\n        \n        r.cooldown:SetReverse(true)\n        \n        if remain == 0 then\n            r.icon:SetDesaturation(1)\n        else\n            local _, _, _, castCB = GetSpellInfo(116858)\n            local demonFire = 3/(1 + UnitSpellHaste(\"player\")*0.01)\n            local c = aura_env.timerParameters.HELPFUL\n            \n            castCB = castCB*0.001\n            \n            if IsPlayerSpell(196447) then\n                if remain < demonFire and demonFire > castCB then\n                    r.icon:SetDesaturation(0)\n                    r.text2:SetTextColor(1, 1, 0, 1)\n                end\n                if remain < castCB then\n                    r.icon:SetDesaturation(1 - remain/castCB)\n                    r.text2:SetTextColor(c.rS, c.gS, c.bS, 1)\n                end\n                if remain > demonFire then\n                    r.icon:SetDesaturation(0)\n                    r.text2:SetTextColor(c.rN, c.gN, c.bN, 1)\n                end\n            else\n                if remain < castCB then\n                    r.icon:SetDesaturation(1 - remain/castCB)\n                    r.text2:SetTextColor(c.rS, c.gS, c.bS, 1)\n                else\n                    r.icon:SetDesaturation(0)\n                    r.text2:SetTextColor(c.rN, c.gN, c.bN, 1)\n                end\n            end\n        end\n    end\n}\n\n--[[ What SpecializedSpells is for:\n\nThis table holds instructions on how to handle the progress/stack display of\n    a spell in a more specific way than that of the main trigger. Every\n    function listed should return seven values, in this order:\n    stacks, duration, expirationTime, maxCharges, pand, icon, type.\n\nStructure:\n\n    SpecializedSpells = {\n        [spellID] = function()\n        --body\n        return stacks, duration, expirationTime, maxCharges, pand, icon, type\n        end,\n    }\n\n]]--\n\naura_env.SpecializedSpells = {\n    [205145] = function() --Demonic Calling (supress stacks)\n        local _, dur, exp = aura_env.AuraInfo(205146, \"HELPFUL\")\n        local icon = GetSpellTexture(205146)\n        \n        return 0, dur, exp, 0, 6, icon, \"HELPFUL\"\n    end,\n    \n    [267217] = function() --Nether Portal active time handling\n        local start, dur = GetSpellCooldown(267217)\n        local icon, type, pand = GetSpellTexture(267217), \"COOLDOWN\", 60\n        local exp = dur > 1.5 and start + dur or 0\n        dur = dur > 1.5 and dur or 0\n        \n        if GetTime() - start < 20 and dur > 0 then\n            type = \"HELPFUL\"\n            exp = start + 20\n            dur = 20\n            pand = 6\n        end\n        \n        return 0, dur, exp, 0, pand, icon, type\n    end\n}\n\n--[[ How KnownSpells Works:\n\n'UpdateKnownSpells' inserts spells from the 'talents' table in the order they\n    are presented. Each entry in this table contains the same corresponding\n    table from 'talents':\n\n    KnownSpells = {\n        {id = spellID, filters = {filtersTable}[, applies = appliesSpellID]}\n    }\n\nSmall caveat: filters are turned into keys with a 'true' value to help with\n    the construction of the aura:\n\n    filters = {\n        COOLDOWN = true,\n        HELPFUL = true\n    }\n\nThis could readily de done in the 'talents' table, but it'd look polluted.\n\n]]--\n\naura_env.KnownSpells = {}\n\nfunction aura_env.UpdateKnownSpells()\n    local ipairs, table = ipairs, table\n    \n    table.wipe(aura_env.KnownSpells)\n    \n    for _, set in ipairs(talents) do\n        for _, spell in ipairs(set) do\n            --Insert info about the spell known in each 'orderPriority' set\n            --into 'KnownSpells'.\n            local check = spell.id\n            \n            if spell.condition and not IsPlayerSpell(spell.condition) then\n                check = spell.condition\n            end\n            \n            if IsPlayerSpell(check) then\n                \n                for filterKey, filterName in ipairs(spell.filters) do\n                    spell.filters[filterKey] = nil\n                    spell.filters[filterName] = true\n                end\n                \n                table.insert(aura_env.KnownSpells, spell)\n                \n                --No need to continue iterating this 'set'\n                break\n            end\n        end\n    end\nend\n\naura_env.UpdateKnownSpells()\naura_env.maxAuras = #aura_env.KnownSpells\n\nfunction aura_env.AuraInfo(spellID, filters)\n    local UnitAura = UnitAura\n    local unit = filters == \"HARMFUL\" and \"target\" or \"player\"\n    filters = filters..\" PLAYER\"\n    \n    for i = 1, 40 do\n        local _, _, stacks, _, dur, exp, _, _, _, id = UnitAura(unit, i, filters)\n        \n        if id and id == spellID then\n            return stacks, dur, exp\n        end\n        \n        if not id then\n            return 0, 0, 0\n        end\n    end\nend\n\nlocal r = aura_env.region\nr.cooldown:SetDrawEdge(true)\nr.stacks:ClearAllPoints()\nr.stacks:SetPoint(\"RIGHT\", r, \"BOTTOM\", 0.45*r.width, 0.075*r.height)",
					["do_custom"] = true,
