						["customVariables"] = "{\n    type = {\n        type = \"select\",\n        display = \"Aura Type\",\n        values = {CHARGE = \"Charge\", COOLDOWN = \"Cooldown\", HELPFUL = \"Buff\", HARMFUL = \"Debuff\"}\n    },\n    name = {\n        type = \"string\",\n        display = \"Aura Name\"\n    },\n    spellID = {\n        type = \"number\",\n        display = \"Aura SpellID\"\n    },\n    debuffNotApplied = {\n        type = \"bool\",\n        display = \"Target Is Not Debuffed\"\n    },\n    inPandemic = {\n        type = \"bool\",\n        display = \"In Pandemic Range\"\n    },\n    isReady = {\n        type = \"bool\",\n        display = \"Spell Is Ready\"\n    },\n    expirationTime = true,\n    duration = true,\n    stacks = true\n}",
