					["translateFunc"] = "function(progress, startX, startY, deltaX, deltaY)\n    local e = aura_env\n    local s, r = e.state, e.region\n    \n    if s and r then\n        if e.SpecializedColoring[s.spellID] then\n            e.SpecializedColoring[s.spellID]()\n        else\n            local SetReverse = SetReverse\n            local dur, exp, type = s.duration, s.expirationTime, s.type\n            local remains = dur > 0 and (exp - GetTime()) or 0\n            local c = e.timerParameters[type]\n            \n            local desatNormal, desatShift, desatReady --0 color, 1 b&w\n            r.cooldown:SetReverse(false)\n            \n            if type == \"HELPFUL\" or type == \"HARMFUL\" then\n                desatNormal, desatShift, desatReady = 0, 1 - remains/s.shift, 1\n                r.cooldown:SetReverse(true)\n            end\n            \n            if type == \"COOLDOWN\" then\n                desatNormal, desatShift, desatReady = 1, remains/s.shift, 0 \n            end\n            \n            if type == \"CHARGE\" then\n                desatNormal, desatReady = 1 - s.stacks/s.maxCharges, 0\n                desatShift = remains/s.shift - s.stacks + 1/s.maxCharges\n            end\n            \n            if remains == 0 then\n                r.cooldown:Hide()\n                r.icon:SetDesaturation(desatReady)\n            elseif remains <= s.shift then\n                --r.cooldown:Show()\n                s.inPandemic = type == \"HARMFUL\"\n                r.icon:SetDesaturation(desatShift)\n                r.text2:SetTextColor(c.rS, c.gS, c.bS, 1)\n            else\n                --r.cooldown:Show()\n                r.icon:SetDesaturation(desatNormal)\n                r.text2:SetTextColor(c.rN, c.gN, c.bN, 1)\n            end\n        end\n        \n        r.cooldown:SetDrawEdge(true)\n        r.stacks:ClearAllPoints()\n        r.stacks:SetPoint(\"RIGHT\", r, \"BOTTOM\", 0.45*r.width, 0.075*r.height)\n        \n        local width = r.icon:GetWidth()\n        \n        return startX + (width + 3)*(s.index - 0.5*(1 + e.maxAuras))\n    end\nend",
