unit = UnitName(unit)\n            \n            if WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==2008 then\n                -- Helya\n                \n                -- Taint of the Sea\n                spell=GetSpellInfo(228054) \n                if UnitDebuff(unit,spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.GREEN_LS, aura_env.ALPHA_M)\n                end\n                -- Orb of Corrosion\n                spell=GetSpellInfo(230267) \n                if UnitDebuff(unit,spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.PURPLE_LS, aura_env.ALPHA_M)\n                end\n                -- Orb of Corruption\n                spell=GetSpellInfo(229119) \n                if UnitDebuff(unit,spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.PURPLE_LS, aura_env.ALPHA_M)\n                end\n                -- Fetid Rot\n                spell=GetSpellInfo(193367) \n                if UnitDebuff(unit,spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.GRAY, aura_env.ALPHA_M)\n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1962 then\n                --Guarm\n                \n                -- Flaming Volatile Foam\n                spell = GetSpellInfo(228744)\n                if UnitDebuff(unit, spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.RED, aura_env.ALPHA_M) \n                end\n                \n                -- Briney Volatile Foam\n                spell = GetSpellInfo(228810)\n                if UnitDebuff(unit, spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, {0, 1, 0.7}, aura_env.ALPHA_M) \n                end\n                \n                -- Shadowy Volatile Foam\n                spell = GetSpellInfo(228818)\n                if UnitDebuff(unit, spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, {0.8, 0.2, 1}, aura_env.ALPHA_M) \n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1865 then\n                --Chronomatic Anomaly\n                \n                -- Time Bomb\n                spell = GetSpellInfo(206617)\n                if UnitDebuff(unit, spell) then\n                    r = 1\n                    g = 1\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.YELLOW, aura_env.ALPHA_M, duration) \n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1867 then\n                --Trilliax\n                \n                -- Sterilize\n                spell = GetSpellInfo(208499)\n                if UnitDebuff(unit, spell) then\n                    if getRange(unit) >= 8 then\n                        aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.YELLOW, aura_env.ALPHA_M) \n                    else\n                        aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.RED, aura_env.ALPHA_M) \n                    end\n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1871 then\n                --Spellblade\n                aura_env.spellblade = (aura_env.spellblade or {{aura_env.GREEN,0},{aura_env.PURPLE,0},{aura_env.ORANGE,0},{aura_env.BLUE,0}})\n                -- Mark of Frost\n                spell = GetSpellInfo(212587)\n                if UnitDebuff(unit, spell)  then\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    local color=nil\n                    -- Find the identical expiration time\n                    for i=1,#(aura_env.spellblade) do\n                        if math.abs(aura_env.spellblade[i][2] - expires) < 1 then \n                            color=aura_env.spellblade[i][1]\n                            break\n                        end\n                    end\n                    -- If no identical expiration time found, add to the table\n                    if color==nil then\n                        for i=1,#(aura_env.spellblade) do\n                            if GetTime()>aura_env.spellblade[i][2] then \n                                aura_env.spellblade[i][2]=expires\n                                color=aura_env.spellblade[i][1]\n                                break\n                            end\n                        end\n                    end\n                    if color==nil then color = aura_env.GRAY end\n                    local _,_,_,frostbittenStacks=UnitDebuff(unit,GetSpellInfo(212647))\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M,frostbittenStacks)\n                end\n                \n                -- Searing Brand\n                spell = GetSpellInfo(213166)\n                if UnitDebuff(unit, spell) then\n                    local role = UnitGroupRolesAssigned(unit)\n                    if role ~= \"TANK\" then\n                        if getRange(unit) >= 13 then\n                            aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.YELLOW, aura_env.ALPHA_M)\n                        else\n                            aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.RED, aura_env.ALPHA_M)\n                        end\n                    end\n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1862 then\n                --Tichondrius\n                \n      