\n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1886 then\n                --Botanist\n                \n                -- Call of Night\n                spell = GetSpellInfo(218809)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if UnitDebuff(player, spell) then\n                        if getRange(unit) >= 8 then\n                            color=aura_env.YELLOW\n                        end\n                    else\n                        if getRange(unit) >= 8 then\n                            color=aura_env.AQUA\n                        else\n                            color=aura_env.GREEN\n                        end\n                    end\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M, duration) \n                end\n                \n                -- Parasitic Fetter\n                spell = GetSpellInfo(218304)\n                if UnitDebuff(unit, spell) then\n                    if getRange(unit) >= 10 then\n                        color=aura_env.YELLOW\n                    else\n                        color=aura_env.RED\n                    end\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.XL, color, aura_env.ALPHA_M) \n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1863 then\n                --Star Augur\n                \n                -- Fel Ejection\n                spell = GetSpellInfo(205649)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if getRange(unit) >= 8 then\n                        color=aura_env.YELLOW\n                    else\n                        color={1,0.6,0}\n                    end\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M, duration) \n                end\n                \n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1872 then\n                --Elisande\n                \n                -- Elisande Beam\n                spell = GetSpellInfo(209244)\n                if UnitDebuff(unit, spell) then\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.YELLOW, aura_env.ALPHA_M, duration)   \n                end\n                \n                -- Ablating Explosion\n                spell = GetSpellInfo(209973)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if getRange(unit) >= 43 then\n                        color=aura_env.GREEN\n                    elseif getRange(unit) >= 22 then\n                        color=aura_env.YELLOW\n                    end\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M, duration)  \n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1866 then\n                --Gul'dan\n                \n                -- Mark tanks\n                if UnitGroupRolesAssigned(unit)==\"TANK\" then\n                    aura_env.addTextureToNameplate(unit, shield, aura_env.L, aura_env.WHITE, aura_env.ALPHA_H)\n         