						["custom"] = "function(a, event)\n    local e = aura_env\n    \n    if event == \"FRAME_UPDATE\" then\n        local time = GetTime()\n        \n        e.last = e.last or time\n        \n        if e.last >= time - 2 then\n            return true\n        end\n        \n        e.last = time\n    end\n    \n    for _, v in pairs(a) do\n        --table.wipe(a)\n        --v = nil\n        v.show = false\n        v.changed = true\n    end\n    \n    local i = 1\n    local GetSpellCooldown = GetSpellCooldown\n    local GoSac = IsPlayerSpell(108503)\n    \n    for k, v in ipairs(e.offense) do\n        if (GoSac and not v[3])\n        or (not GoSac and v[1] ~= 119898)then\n            local start, dur = 0, 0\n            \n            if GoSac and v[1] == 119898 then\n                v[2] = GetSpellTexture(v[1])\n                \n                for _, pet in ipairs(e.GoSac) do\n                    local startTemp, durTemp = GetSpellCooldown(pet)\n                    local check = GetSpellTexture(pet)\n                    \n                    if durTemp > 1.5 and v[2] == check then\n                        start, dur = startTemp, durTemp\n                        break\n                    end\n                end\n            elseif IsSpellKnown(v[1], v[3]) then\n                start, dur = GetSpellCooldown(v[1])\n            end\n            \n            if dur > 1.5 then\n                a[i] = {\n                    show = dur > 1.5,\n                    changed = dur > 1.5,\n                    icon = v[2],\n                    progressType = \"timed\",\n                    duration = dur,\n                    expirationTime = start + dur,\n                    autoHide = true,\n                    index = i\n                }\n                \n                i = i + 1\n            end\n        end\n    end\n    \n    return true\nend",
