						["custom"] = "function(allstates, event, unit)\n    local _, _, class = UnitClass(\"player\")\n    local raceName, raceFile, raceID = UnitRace(\"player\")\n    if event == \"NAME_PLATE_UNIT_ADDED\" or event == \"UNIT_AURA\" then\n        if UnitExists(unit) then\n            if unit ~= \"target\"  and UnitCanAttack(\"player\", unit) and not UnitPlayerControlled(unit) then\n                for _, state in pairs(allstates) do\n                    if UnitIsUnit(state.unit, unit) then\n                        state.changed = true\n                        state.show = false\n                    end\n                end\n                local count, name, icon, stacks, debuffType, duration, expirationTime, spellID = 0 \n                for i = 1, 255 do\n                    name, icon, stacks, debuffType, duration, expirationTime, _,stealable,_, spellID = UnitAura(unit, i) \n                    if not name then break end\n                    if (stealable and (aura_env.magicclass[class] or raceID == 10)) or (debuffType == \"\" and aura_env.enrageclass[class]) then\n                        allstates[unit..count] = {\n                            changed = true,\n                            show = true,\n                            stacks = stacks,\n                            icon = icon,\n                            progressType = \"timed\",\n                            duration = duration,\n                            expirationTime = expirationTime,\n                            unit = unit,\n                            offset = count*aura_env.region.width,\n                            autoHide = true,\n                        }\n                        count = count+1\n                    end\n                end\n            end\n        end\n    end\n    if event == \"NAME_PLATE_UNIT_REMOVED\" then\n        if UnitExists(unit) then\n            for _, state in pairs(allstates) do\n                if state.unit == unit then\n                    state.show = false\