					["colorFunc"] = "function(progress, r1, g1, b1, a1, r2, g2, b2, a2)\n    if aura_env then\n        aura_env.updateIPValues()\n        \n        local region = WeakAuras.regions[aura_env.id].region\n        local bar = region.bar\n        \n        local sizePerPower\n        if aura_env.orientation == \"HORIZONTAL\" or aura_env.orientation == \"HORIZONTAL_INVERSE\" then\n            sizePerPower = bar:GetWidth() / aura_env.cap\n        elseif aura_env.orientation == \"VERTICAL\" or aura_env.orientation == \"VERTICAL_INVERSE\" then\n            sizePerPower = bar:GetHeight() / aura_env.cap\n        end\n        \n        --text fields\n        local textLeft = region.text\n        local textCenter = bar.centerText\n        local textRight = region.timer\n        textLeft:SetText(aura_env.replaceTokens(aura_env.textLeft))\n        textCenter:SetText(aura_env.replaceTokens(aura_env.textCenter))\n        textRight:SetText(aura_env.replaceTokens(aura_env.textRight))\n        \n        textCenter:SetFont(\n            aura_env.theme.textCenterFontName,\n            aura_env.theme.textCenterFontSize,\n            aura_env.theme.textCenterFontFlags\n        )\n        textCenter:SetPoint(\n            \"CENTER\",\n            bar,\n            \"CENTER\",\n            textCenter.offset,\n            textCenter.offset\n        )\n        aura_env.rotateText(textCenter)\n        textCenter:Show()\n        \n        --castIP bar\n        local castIPnotnegative = aura_env.castIP\n        castIPnotnegative = math.max(aura_env.castIP, 0)\n        if castIPnotnegative > 0 and (aura_env.theme.alwaysShowCastBar or aura_env.hasMinRage) then\n            local castWidth, castHeight = 0, 0\n            local castSize = sizePerPower * castIPnotnegative\n            \n            if aura_env.orientation == \"HORIZONTAL\" or aura_env.orientation == \"HORIZONTAL_INVERSE\" then\n                castWidth = castSize\n                castHeight = bar:GetHeight()\n            elseif aura_env.orientation == \"VERTICAL\" or aura_env.orientation == \"VERTICAL_INVERSE\" then\n                castHeight = castSize\n                castWidth = bar:GetWidth()\n            end\n            \n            bar.cast:SetPoint(\n                aura_env.point,\n                bar.fg,\n                aura_env.castPoint,\n                1*aura_env.offsetXMulti,\n                1*aura_env.offsetYMulti\n            )\n            bar.cast:SetWidth(castWidth)\n            bar.cast:SetHeight(castHeight)\n            if aura_env.castIP < aura_env.newIP then\n                bar.cast:SetVertexColor(aura_env.HexToRGB(aura_env.theme.colorCastIPWaste))\n            else\n                bar.cast:SetVertexColor(aura_env.HexToRGB(aura_env.theme.colorCastIP))\n            end\n            bar.cast:Show()\n        else\n            bar.cast:Hide()\n        end\n        \n        --spark\n        if aura_env.theme.showTimeSpark and aura_env.duration and aura_env.duration > 0 then\n            local progress = (aura_env.expiration - GetTime()) / aura_env.duration\n            local sparkOffsetX, sparkOffsetY\n            if aura_env.orientation == \"HORIZONTAL\" or aura_env.orientation == \"HORIZONTAL_INVERSE\" then\n                sparkOffsetX = bar.fg:GetWidth() * progress\n                sparkOffsetY = 0\n            elseif aura_env.orientation == \"VERTICAL\" or aura_env.orientation == \"VERTICAL_INVERSE\" then\n                sparkOffsetX = 0\n                sparkOffsetY = bar.fg:GetHeight() * progress\n            end\n            bar.sparkTime:ClearAllPoints()\n            bar.sparkTime:SetPoint(\n                aura_env.point,\n                bar.fg,\n                aura_env.point,\n                aura_env.offsetXMulti*sparkOffsetX,\n                aura_env.offsetYMulti*sparkOffsetY\n            )\n            bar.sparkTime:Show()\n        else\n            bar.sparkTime:Hide()\n        end\n        \n        if aura_env.curIP > aura_env.cap then\n            return aura_env.HexToRGB(aura_env.theme.colorCurIPOverCap)\n        else\n            return aura_env.HexToRGB(aura_env.theme.colorCurIP)\n        end\n    end\n    return r1, g1, b1, a1\nend",
