				["unit"] = "player",
				["subeventSuffix"] = "_CAST_START",
				["custom"] = "-- Some original Code in Action-tab made by Pottm@Serenity from his Star Augur weakaura\n-- Rewritten by (EU)Relodr-Blackrock - Reloe#2787 to make it work for other abilities and include some more features.\n-- Re-rewritten by (US)Bosmutus - Zul'jin\n\nfunction ()\n    -- Limit scan rate to improve performance\n    if GetTime()-(aura_env.LastScan or 0) < 0.3 then return end \n    aura_env.LastScan=GetTime()\n    \n    local unit = \"\"\n    local spell = \"\"\n    local spell2 = \"\"\n    local player = UnitName(\"player\")\n    local distance = -1\n    local duration = nil\n    local width = 60\n    local r = 1\n    local g = 0\n    local b = 0\n    local a = 0.5\n    local circle = \"Interface\\\\AddOns\\\\WeakAuras\\\\Media\\\\Textures\\\\Circle_White.tga\"\n    local shield = \"Interface\\\\Addons\\\\WeakAuras\\\\PowerAurasMedia\\\\Auras\\\\Aura21.tga\"\n    local handicap = \"Interface\\\\PVPFrame\\\\PVP-Banner-Emblem-1.tga\"\n    local jesus = \"Interface\\\\Addons\\\\WeakAuras\\\\PowerAurasMedia\\\\Auras\\\\Aura113.tga\"\n    \n    -- Only look up range when needed, and cache the value. \n    local getRange=function(unit)\n        if distance == -1 then distance = aura_env.rangeCheck(unit) end\n        return distance\n    end\n    \n    aura_env.everyFrame()\n    \n    if aura_env.debug then\n        --aura_env.addTextureToNameplate(\"target\", shield, 60, 0.75, 0.75, 0.75, 0.9, -1)\n        --aura_env.addTextureToNameplate(\"target\", handicap, aura_env.M, {1, 1, 1}, 1, nil, nil, 1)\n        --aura_env.addTextureToNameplate(\"target\", circle, aura_env.L, {0, 1, 1}, a, 7)\n        aura_env.addTextureToNameplate(\"target\", jesus, aura_env.S, {0.27,0.17,0.125}, 1, nil, nil, 1)\n        aura_env.addTextureToNameplate(\"target\", circle, aura_env.L, aura_env.WHITE, aura_env.ALPHA_M)\n    end\n    \n    for i=1, GetNumGroupMembers() do\n        distance = -1\n        unit = GetRaidRosterInfo(i)\n        if UnitExists(unit) then\n            unit = UnitName(unit)\n            \n            if WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==2008 then\n                -- Helya\n                \n                -- Taint of the Sea\n                spell=GetSpellInfo(228054) \n                if UnitDebuff(unit,spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.GREEN_LS, aura_env.ALPHA_M)\n                end\n                -- Orb of Corrosion\n                spell=GetSpellInfo(230267) \n                if UnitDebuff(unit,spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.PURPLE_LS, aura_env.ALPHA_M)\n                end\n                -- Orb of Corruption\n                spell=GetSpellInfo(229119) \n                if UnitDebuff(unit,spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.PURPLE_LS, aura_env.ALPHA_M)\n                end\n                -- Fetid Rot\n                spell=GetSpellInfo(193367) \n                if UnitDebuff(unit,spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.GRAY, aura_env.ALPHA_M)\n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1962 then\n                --Guarm\n                \n                -- Flaming Volatile Foam\n                spell = GetSpellInfo(228744)\n                if UnitDebuff(unit, spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.RED, aura_env.ALPHA_M) \n                end\n                \n                -- Briney Volatile Foam\n                spell = GetSpellInfo(228810)\n                if UnitDebuff(unit, spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, {0, 1, 0.7}, aura_env.ALPHA_M) \n                end\n                \n                -- Shadowy Volatile Foam\n                spell = GetSpellInfo(228818)\n                if UnitDebuff(unit, spell) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, {0.8, 0.2, 1}, aura_env.ALPHA_M) \n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1865 then\n                --Chronomatic Anomaly\n                \n                -- Time Bomb\n                spell = GetSpellInfo(206617)\n                if UnitDebuff(unit, spell) then\n                    r = 1\n                    g = 1\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.YELLOW, aura_env.ALPHA_M, duration) \n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1867 then\n                --Trilliax\n                \n                -- Sterilize\n                spell = GetSpellInfo(208499)\n                if UnitDebuff(unit, spell) then\n                    if getRange(unit) >= 8 then\n                        aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.YELLOW, aura_env.ALPHA_M) \n                    else\n                        aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.RED, aura_env.ALPHA_M) \n                    end\n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1871 then\n                --Spellblade\n                aura_env.spellblade = (aura_env.spellblade or {{aura_env.GREEN,0},{aura_env.PURPLE,0},{aura_env.ORANGE,0},{aura_env.BLUE,0}})\n                -- Mark of Frost\n                spell = GetSpellInfo(212587)\n                if UnitDebuff(unit, spell)  then\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    local color=nil\n                    -- Find the identical expiration time\n                    for i=1,#(aura_env.spellblade) do\n                        if math.abs(aura_env.spellblade[i][2] - expires) < 1 then \n                            color=aura_env.spellblade[i][1]\n                            break\n                        end\n                    end\n                    -- If no identical expiration time found, add to the table\n                    if color==nil then\n                        for i=1,#(aura_env.spellblade) do\n                            if GetTime()>aura_env.spellblade[i][2] then \n                                aura_env.spellblade[i][2]=expires\n                                color=aura_env.spellblade[i][1]\n                                break\n                            end\n                        end\n                    end\n                    if color==nil then color = aura_env.GRAY end\n                    local _,_,_,frostbittenStacks=UnitDebuff(unit,GetSpellInfo(212647))\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M,frostbittenStacks)\n                end\n                \n                -- Searing Brand\n                spell = GetSpellInfo(213166)\n                if UnitDebuff(unit, spell) then\n                    local role = UnitGroupRolesAssigned(unit)\n                    if role ~= \"TANK\" then\n                        if getRange(unit) >= 13 then\n                            aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.YELLOW, aura_env.ALPHA_M)\n                        else\n                            aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.RED, aura_env.ALPHA_M)\n                        end\n                    end\n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1862 then\n                --Tichondrius\n                \n                -- Carrion Plague\n                spell = GetSpellInfo(206480)\n                spell2 = GetSpellInfo(206311)\n                if UnitDebuff(unit, spell) and not UnitDebuff(player, spell2) then\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.M, aura_env.PURPLE, aura_env.ALPHA_M)\n                end\n                \n                \n                -- Burning Soul\n                spell = GetSpellInfo(216040)\n                if UnitDebuff(unit, spell) then\n                    if getRange(unit) >= 10 then\n                        aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.YELLOW, aura_env.ALPHA_M)\n                    else\n                        aura_env.addTextureToNameplate(unit, circle, aura_env.M, aura_env.RED, aura_env.ALPHA_M)\n                    end\n                end\n                \n                -- Show Immunities\n                if UnitBuff(unit,GetSpellInfo(642)) or UnitBuff(unit,GetSpellInfo(45438)) or UnitBuff(unit,GetSpellInfo(186265)) or UnitBuff(unit,GetSpellInfo(31224)) or UnitBuff(unit,GetSpellInfo(48707)) or UnitBuff(unit,GetSpellInfo(23920)) then\n                    aura_env.addTextureToNameplate(unit, jesus, aura_env.S, {0.27,0.17,0.125}, 1, nil, nil, 1)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.WHITE, aura_env.ALPHA_M)\n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1842 then\n                --Krosus\n                \n                -- Orb of Destruction\n                spell = GetSpellInfo(205344)\n                if UnitDebuff(unit, spell) then\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.M, aura_env.RED, aura_env.ALPHA_M, duration)\n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1886 then\n                --Botanist\n                \n                -- Call of Night\n                spell = GetSpellInfo(218809)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if UnitDebuff(player, spell) then\n                        if getRange(unit) >= 8 then\n                            color=aura_env.YELLOW\n                        end\n                    else\n                        if getRange(unit) >= 8 then\n                            color=aura_env.AQUA\n                        else\n                            color=aura_env.GREEN\n                        end\n                    end\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M, duration) \n                end\n                \n                -- Parasitic Fetter\n                spell = GetSpellInfo(218304)\n                if UnitDebuff(unit, spell) then\n                    if getRange(unit) >= 10 then\n                        color=aura_env.YELLOW\n                    else\n                        color=aura_env.RED\n                    end\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.XL, color, aura_env.ALPHA_M) \n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1863 then\n                --Star Augur\n                \n                -- Fel Ejection\n                spell = GetSpellInfo(205649)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if getRange(unit) >= 8 then\n                        color=aura_env.YELLOW\n                    else\n                        color={1,0.6,0}\n                    end\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M, duration) \n                end\n                \n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1872 then\n                --Elisande\n                \n                -- Elisande Beam\n                spell = GetSpellInfo(209244)\n                if UnitDebuff(unit, spell) then\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, aura_env.YELLOW, aura_env.ALPHA_M, duration)   \n                end\n                \n                -- Ablating Explosion\n                spell = GetSpellInfo(209973)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if getRange(unit) >= 43 then\n                        color=aura_env.GREEN\n                    elseif getRange(unit) >= 22 then\n                        color=aura_env.YELLOW\n                    end\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M, duration)  \n                end\n                \n            elseif WeakAuras.CurrentEncounter and WeakAuras.CurrentEncounter.id==1866 then\n                --Gul'dan\n                \n                -- Mark tanks\n                if UnitGroupRolesAssigned(unit)==\"TANK\" then\n                    aura_env.addTextureToNameplate(unit, shield, aura_env.L, aura_env.WHITE, aura_env.ALPHA_H)\n                end\n                \n                -- Well of Soul\n                -- spell = GetSpellInfo(208536)\n                -- if UnitDebuff(unit, spell) then\n                --     if getRange(unit) >= 5 then\n                --         a = 0.3\n                --         r = 1\n                --         g = 1\n                --         b = 0\n                --     else\n                --         g = 0\n                --     end\n                --     aura_env.addTextureToNameplate(unit, circle, width, r, g, b, a, duration)       \n                -- end\n                \n                -- Bonds of Fel\n                spell = GetSpellInfo(209011)\n                if UnitDebuff(unit, spell) then\n                    local color = aura_env.GREEN\n                    if getRange(unit) >= 8 then\n                        color=aura_env.AQUA\n                    end\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.XL, color, aura_env.ALPHA_M)\n                    aura_env.addTextureToNameplate(unit, handicap, aura_env.S, aura_env.WHITE, 1, nil, nil, 1)\n                end\n                \n                -- Empowered Bonds of Fel\n                spell = GetSpellInfo(206384)\n                if UnitDebuff(unit, spell) then\n                    local color = aura_env.GREEN\n                    if getRange(unit) >= 8 then\n                        color=aura_env.AQUA\n                    end\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.XL, color, aura_env.ALPHA_M)\n                    aura_env.addTextureToNameplate(unit, handicap, aura_env.S, aura_env.WHITE, 1, nil, nil, 1)\n                end\n                \n                -- Flames of Sargeras 1\n                spell = GetSpellInfo(221606)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if getRange(unit) >= 10 then\n                        color=aura_env.YELLOW\n                    end\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M, duration)\n                end\n                \n                -- Flames of Sargeras 2\n                spell = GetSpellInfo(221603)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if getRange(unit) >= 10 then\n                        color=aura_env.YELLOW\n                    end\n                    local _, _, _, _, _, _, expires = UnitDebuff(unit, spell)\n                    duration = aura_env.round(expires - GetTime(), 0)\n                    aura_env.addTextureToNameplate(unit, circle, aura_env.L, color, aura_env.ALPHA_M, duration)\n                end\n                \n                -- Eye of Gul'dan 1\n                spell = GetSpellInfo(209454)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if getRange(unit) >= 10 then\n                        color={0.53,0.8,0.92}\n                    end\n                    aura_env.addTextureToNameplate(unit, circle, width, color, aura_env.ALPHA_M)    \n                end\n                \n                -- Eye of Gul'dan 2\n                spell = GetSpellInfo(221728)\n                if UnitDebuff(unit, spell) then\n                    local color=aura_env.RED\n                    if getRange(unit) >= 10 then\n                        color={0.53,0.8,0.92}\n                    end\n                    aura_env.addTextureToNameplate(unit, circle, width, color, aura_env.ALPHA_M)    \n                end\n            end\n        end\n    end \nend",
