				["init"] = {
					["do_custom"] = true,
					["custom"] = "aura_env.showAbsolute = false --set to true to show absolute heal value instead of percentage\n\naura_env.lastSec = 5\naura_env.percDmg = 0.2\naura_env.minHealthPerc = 0.1\n\naura_env.table = {}\n\naura_env.shortenNumber = function(number)\n    local affixes = {\n        \"\",\n        \"k\",\n        \"m\",\n        \"b\",\n    }\n    \n    local affix = 1\n    local dec = 0\n    while number >= 1000 and affix < #affixes do\n        number = number / 1000\n        affix = affix + 1\n    end\n    if affix > 1 then\n        dec = 2\n        local num2 = number\n        while num2 >= 10 do\n            num2 = num2 / 10\n            dec = dec - 1\n        end\n    end\n    \n    return string.format(\"%.\"..dec..\"f\"..affixes[affix], number)\nend",
