					["colorFunc"] = "function(progress, r1, g1, b1, a1, r2, g2, b2, a2)\n    local textr, textg, textb, texta = WeakAuras.regions[aura_env.id].region.text:GetTextColor()\n    local fontName, fontHeight, fontFlags = WeakAuras.regions[aura_env.id].region.text:GetFont()\n    local red, green, blue, alpha = 1, 1, 1, 1\n    local fragCount = UnitPower(\"player\",  Enum.PowerType.SoulShards, true);\n    local specID = GetSpecialization()\n    local colourR, colourG, colourB, colourA = 1, 1, 1, 1\n    local shardValue = shardValue or 0\n    local shardDisplayCheck = false\n    local shardValue = 1\n    \n    aura_env.ssCount = UnitPower(\"player\",  Enum.PowerType.SoulShards, false);\n    aura_env.fragCount = UnitPower(\"player\",  Enum.PowerType.SoulShards, true);\n    aura_env.specID = GetSpecialization()\n    aura_env.recurrentRitual = IsEquippedItem(132393)\n    \n    if soulShard_text then\n        soulShard_text:SetWordWrap(false)\n        soulShard_text:SetShadowOffset(soulShard_textShadowX, soulShard_textShadowY)\n        soulShard_text:SetFont(fontName, fontHeight, fontFlags)            \n        soulShard_text:SetTextColor(textr, textg, textb, texta)\n        soulShard_text:SetText(soulShard_shardAmount())\n    end\n    \n    if soulShard_textDisplay[specID] == true then\n        soulShard_text:Show()\n        local shardID = \"\"\n        if soulShard_textPosition ~= \"FREE\" then\n            if soulShard_centerCounter ~= 0 then\n                if  math.ceil(aura_env.fragCount/10) >= soulShard_centerCounter or soulShard_emptyShards == true then\n                    shardID = \"SoulShard\"..soulShard_centerCounter\n                    soulShard_text:ClearAllPoints()\n                    soulShard_text:SetPoint(soulShard_textPosition, WeakAuras.regions[shardID].region, soulShard_textPosition, 0, 0)\n                else \n                    soulShard_text:Hide()\n                end\n            elseif soulShard_centerCounter == 0 then\n                if aura_env.specID == 3 then\n                    shardID = \"SoulShard\"..math.ceil(aura_env.fragCount/10)\n                else\n                    shardID = \"SoulShard\"..aura_env.ssCount\n                end\n                if aura_env.specID ~= 3 then \n                    aura_env.fragCount = 0 \n                end\n                \n                if aura_env.ssCount > 0 or aura_env.fragCount > 0 then\n                    soulShard_text:ClearAllPoints()\n                    soulShard_text:SetPoint(soulShard_textPosition, WeakAuras.regions[shardID].region, soulShard_textPosition, 0, 0)\n                else \n                    soulShard_text:Hide()\n                end\n                \n            end\n        elseif soulShard_textPosition == \"FREE\" then\n            soulShard_text:ClearAllPoints()\n            soulShard_text:SetPoint(\"CENTER\", WeakAurasFrame, \"CENTER\", soulShard_textPositionX, soulShard_textPositionY)\n        end\n        \n    elseif soulShard_textDisplay[specID] == false then\n        \n        soulShard_text:Hide()\n    end\n    \nend",
