nction(x,y)\n                            if x[4] and y[4] then\n                                return x[4] < y[4]\n                            else return true\n                            end\n                    end)\n                end\n            end\n            \n        elseif type == \"UNIT_DIED\" then\n            \n            if destGUID == soulShard_timerInfo[destGUID][7] then\n                table.remove(soulShard_timerInfo[destGUID])\n            end\n            \n        elseif type == \"SPELL_AURA_REMOVED\" or type == \"SPELL_INSTAKILL\" then \n            \n            if spellid == 603 then\n                \n                Info[destGUID] = nil\n                \n                wipe(soulShard_sortedTable)\n                \n                for _, v in pairs(soulShard_timerInfo) do\n                    table.insert(soulShard_sortedTable, v)\n                end\n                if soulShard_sortedTable and soulShard_sortedTable[2] and soulShard_sortedTable[2][4] then\n                    table.sort(soulShard_sortedTable, function(x,y)\n                            if x[4] and y[4] then\n                                return x[4] < y[4]\n                            else return true\n                            end\n                    end)\n                end\n            end\n        end\n        \n        --[[if soulShard_timerInfo then\n            for k,v in pairs(soulShard_timerInfo) do \n                for i,j in pairs(v) do \n                    if i == 4 then\n                        print(j, GetTime())\n                    end\n                end\n            end\n        end]]\n    end\nend",
