					["colorFunc"] = "function(progress, r1, g1, b1, a1, r2, g2, b2, a2)\n    if aura_env then\n        aura_env.updateIPValues()\n        \n        local region = WeakAuras.regions[aura_env.id].region\n        local bar = region.bar\n        \n        local sizePerPower\n        if aura_env.orientation == \"HORIZONTAL\" or aura_env.orientation == \"HORIZONTAL_INVERSE\" then\n            sizePerPower = bar:GetWidth() / aura_env.cap\n        elseif aura_env.orientation == \"VERTICAL\" or aura_env.orientation == \"VERTICAL_INVERSE\" then\n            sizePerPower = bar:GetHeight() / aura_env.cap\n        end\n        \n        --text fields\n        local textLeft = region.text\n        local textCenter = bar.centerText\n        local textRight = region.timer\n        textLeft:SetText(aura_env.replaceTokens(aura_env.textLeft))\n        textCenter:SetText(aura_env.replaceTokens(aura_env.textCenter))\n        textRight:SetText(aura_env.replaceTokens(aura_env.textRight))\n        \n        textCenter:SetFont(\n            aura_env.theme.textCenterFontName,\n            aura_env.theme.textCenterFontSize,\n            aura_env.theme.textCenterFontFlags\n        )\n        textCenter:SetPoint(\n            \"CENTER\",\n            bar,\n            \"CENTER\",\n            textCenter.offset,\n            textCenter.offset\n        )\n        aura_env.rotateText(textCenter)\n        textCenter:Show()\n        \n        --castIP bar\n        local castIPnotnegative = aura_env.castIP\n        castIPnotnegative = math.max(aura_env.castIP, 0)\n        if castIPnotnegative > 0 and (aura_env.theme.alwaysShowCastBar or aura_env.hasMinRage) then\n            local castWidth, castHeight = 0, 0\n            local castSize = sizePerPower * castIPnotnegative\n            \n            if aura_env.orientation == \"HORIZONTAL\" or aura_env.orientation == \"HORIZONTAL_INVERSE\" then\n                castWidth = castSize\n                castHeight = bar:GetHeight()\n            elseif aura