				["custom"] = "function()\n    local orbsUp = 0\n    local markIndex = 1\n    for i=1,40 do\n        local unit = \"nameplate\"..i \n        -- print(unit)\n        if UnitExists(unit) then\n            local guid = UnitGUID(unit)\n            local type, zero, server_id, instance_id, zone_uid, npc_id, spawn_uid = strsplit(\"-\",guid)\n            -- print(npc_id)\n            if aura_env.npcID[tonumber(npc_id)] then\n                -- print('a')\n                orbsUp = orbsUp + 1\n                aura_env.glow(unit,true)\n                if aura_env.marks and not GetRaidTargetIndex(unit) and not aura_env.marksBlocked then \n                    SetRaidTarget(unit,markIndex)\n                    if not GetRaidTargetIndex(unit) then\n                        aura_env.marksBlocked = true\n                        C_Timer.After(0.1,function() WeakAuras.ScanEvents('WA_RETRYMARKS') end)\n                    end\n                end\n                markIndex = markIndex<=8 and markIndex+1 or 1\n            else\n                aura_env.glow(unit,false)\n            end\n        end\n    end\n    aura_env.orbs = orbsUp\n    return aura_env.icon and orbsUp > 0\nend",
