				["init"] = {
					["custom"] = "------------- Custom Settings --------------\n-- Only set these if your nameplate addon --\n-- does not have such functionalites -------\n\n-- Hide friendly health bars\naura_env.Force_No_FriendlyBars=false\n-- Multiple nameplates will overlap instead of getting pushed up\naura_env.Force_No_Stacking=false\n-- Make friendly names non-clickable\naura_env.Force_Friendly_Clickthrough=false\n-- Increase nameplate load distance\naura_env.Force_Max_Distance=false\n--------------------------------------------\n\naura_env.Jesus_Color=\"BLACK\"\n\n-- Nameplate setup\nif aura_env.Force_No_Stacking then \n    SetCVar(\"nameplateMotion\",0)\nend\n\nif aura_env.Force_Friendly_Clickthrough then\n    C_NamePlate.SetNamePlateFriendlyClickThrough(true)\nend\n\nif aura_env.Force_Max_Distance then\n    SetCVar(\"nameplateMaxDistance\",70)\nend\n\n-- Initialize variables\naura_env.texturePool = {}\naura_env.textureIndex = 0\naura_env.debug=false\n\n-- Color constants\naura_env.WHITE={1,1,1}\naura_env.RED={1,0,0}\naura_env.GREEN={0,1,0}\naura_env.BLUE={0,0,1}\naura_env.YELLOW={1,1,0}\naura_env.PURPLE={1,0,1}\naura_env.AQUA={0,1,1}\naura_env.ORANGE={1,0.5,0}\n-- Low saturation\naura_env.GRAY={0.75,0.75,0.75}\naura_env.GREEN_LS={0.53,0.75,0.53}\naura_env.PURPLE_LS={0.75,0.53,0.75}\naura_env.BLUE_LS={0.53,0.64,0.75}\naura_env.BROWN_LS={0.75,0.64,0.53}\naura_env.RED_LS={0.75,0.53,0.53}\naura_env.YELLOW_LS={0.75,0.75,0.53}\n\n-- Size constants\naura_env.XL=80\naura_env.L=60\naura_env.M=50\naura_env.S=40\n\n-- Alpha constants\naura_env.ALPHA_H=0.7\naura_env.ALPHA_M=0.5\naura_env.ALPHA_L=0.3\n\n-- Functions\naura_env.getTextureFromPool = function()\n    aura_env.textureIndex = aura_env.textureIndex + 1\n    if aura_env.textureIndex > #aura_env.texturePool then\n        local frame = CreateFrame(\"Frame\") --, nil, UIParent)\n        frame:SetFrameStrata(\"LOW\")\n        frame:SetFrameLevel(0)\n        frame:SetPoint(\"CENTER\", 0, 0)\n        frame:Hide()\n        frame.icon = frame:CreateTexture(nil, \"BACKGROUND\", nil, -8)\n        frame.icon:SetAllPoints()\n        frame.string = frame:CreateFontString(nil, \"BACKGROUND\", nil, -7)\n        frame.string:SetFont(\"Fonts\\\\FRIZQT__.TTF\", 25, \"OUTLINE\")\n        frame.string:SetPoint(\"CENTER\", frame, \"CENTER\", 0, 0)\n        aura_env.texturePool[aura_env.textureIndex] = frame\n    end\n    return aura_env.texturePool[aura_env.textureIndex]\nend\n\naura_env.addTextureToNameplate = function(unit, icon, width, color, alpha, text, textPos, framelvl)\n    if not unit then return end\n    local nameplate = C_NamePlate.GetNamePlateForUnit(unit)\n    if not framelvl then framelvl=0 end\n    if nameplate or UnitIsUnit(\"player\",unit) then\n        local frame = aura_env.getTextureFromPool()\n        frame:ClearAllPoints()\n        if nameplate then\n            frame:SetPoint(\"CENTER\", nameplate, \"CENTER\", 0, -40) \n        else\n            frame:SetPoint(\"CENTER\", nameplate, \"CENTER\", 0, -15) \n        end\n        frame:SetSize(width, width)\n        if type(alpha)==\"table\" then\n            local a1,a2,t = alpha[1],alpha[2],alpha[3]\n            frame.alpha={[\"a1\"]=a1, [\"a2\"]=a2, [\"t\"]=t}\n            frame.icon:SetVertexColor(color[1], color[2], color[3],math.abs(GetTime() % t - t / 2) / (t / 2) * (a2-a1) + a1)\n        else\n            frame.alpha=nil\n            frame.icon:SetVertexColor(color[1], color[2], color[3], alpha)\n        end\n        frame.icon:SetTexture(icon)\n        if color[4] then frame.icon:SetBlendMode(color[4]) end\n        frame:SetFrameLevel(framelvl)\n        if text ~= nil then\n            frame.string:SetText(text)\n        else\n            frame.string:SetText(\"\")\n        end\n        if textPos ~= nil then\n            frame.string:SetPoint(textPos, frame, textPos, 0, 0)\n        end\n        frame:Show()\n    end\nend\n\naura_env.everyFrame = function()\n    for i=1, #aura_env.texturePool do\n        aura_env.texturePool[i]:Hide()\n    end\n    aura_env.textureIndex = 0\nend\n\naura_env.updateFlash = function()\n    if GetTime()-(aura_env.LastFlashUpdate or 0) < 0.02 then return end \n    aura_env.LastFlashUpdate=GetTime()\n    for i=1, #aura_env.texturePool do\n        local f = aura_env.texturePool[i]\n        if f:IsShown() and f.alpha then\n            local a1,a2,t = f.alpha[\"a1\"],f.alpha[\"a2\"],f.alpha[\"t\"]\n            local r,g,b = f.icon:GetVertexColor()\n            f.icon:SetVertexColor(r,g,b,math.abs(GetTime() % t - t / 2) / (t / 2) * (a2-a1) + a1)\n        end     \n    end\nend\n\naura_env.rangeCheck = function(uId)\n    -- TODO: To imporve preformance, we should save off the last value and start searching from there.\n    local range = 0\n    if IsItemInRange(37727, uId) then range = 5--Ruby Acorn\n    elseif IsItemInRange(63427, uId) then range = 8--Worgsaw\n    elseif CheckInteractDistance(uId, 3) then range = 10\n    elseif CheckInteractDistance(uId, 2) then range = 11\n    elseif IsItemInRange(32321, uId) then range = 13--reports 12 but actual range tested is 13\n    elseif IsItemInRange(6450, uId) then range = 18--Bandages.\n    elseif IsItemInRange(21519, uId) then range = 22--Item says 20, returns true until 22.\n    elseif CheckInteractDistance(uId, 1) then range = 30\n    elseif UnitInRange(uId) then range = 43\n    elseif IsItemInRange(116139, uId)  then range = 50\n    elseif IsItemInRange(32825, uId) then range = 60\n    elseif IsItemInRange(35278, uId) then range = 80\n    else range = 1000 end--Just so it has a numeric value, even if it's unknown to protect from nil errors\n    return range\nend\n\naura_env.round = function(num, idp)\n    local mult = 10^(idp or 0)\n    return math.floor(num * mult + 0.5) / mult\nend",
