				["custom"] = "function(_,_,combatEvent,_,sourceGUID,sourceName,_,_,destGUID,destName,_,_,spellID,spell,_,amount)\n    local time = GetTime()\n    \n    --print(aura_env.pguid,sourceGUID,destName,spellID,spell,amount)\n    if sourceGUID == aura_env.pguid and spellID == 211715 then\n        --print(\"TKC! WA estimate (non-dwarf crit): \",string.format(\"%.0f\",WA_OOMM_TKCF_DAMAGE_RAW),\"(\",string.format(\"%.0f\", WA_OOMM_TKCF_DAMAGE_RAW*2),\") Actual: \",amount)\n        --print(aura_env.TotalDemonHealth(),aura_env.DemonicEmpowerment(),aura_env.tkcHealthRatio, aura_env.ShadowMultiplier(),aura_env.LingeringPower(),aura_env.Versatility())\n    end\n    \n    if sourceGUID == aura_env.pguid and combatEvent == \"SPELL_SUMMON\" then\n        --print(combatEvent,spellID,spell,destName)\n        if aura_env.demons[spellID] then\n            aura_env.AddDemon(spellID,destGUID) \n        end\n        \n    end\n    if spellID == 193396 and destGUID ~= UnitGUID(\"pet\") then\n        if combatEvent == \"SPELL_AURA_APPLIED\" or combatEvent == \"SPELL_AURA_REFRESH\" then\n            --print(combatEvent,destGUID)\n            aura_env.DeSet(destGUID,true)\n            return true\n        end\n        if combatEvent == \"SPELL_AURA_REMOVED\" then\n            aura_env.DeSet(destGUID,false)\n            return true\n        end\n    end\n    \n    if destGUID == aura_env.pguid then\n        \n        if combatEvent == \"SPELL_AURA_APPLIED\" or combatEvent == \"SPELL_AURA_REFRESH\" then\n            \n            if spellID == 171982 then\n                aura_env.buff_gds.time = time\n            end\n            if spellID == 196098 then\n                aura_env.buff_sh.time = time\n            end\n            if spellID == 208871 then\n                aura_env.buff_ss.time = time\n            end\n            \n        end\n        if spellID == 236200 and ( combatEvent == \"SPELL_AURA_APPLIED\" or combatEvent == \"SPELL_AURA_APPLIED_DOSE\" or combatEvent == \"SPELL_AURA_