					["custom"] = "aura_env.tkcHealthRatio = 0.08\n\n-- gear check, will need reinit aura (/reload ui) after gear change\nlocal t19 = {}\nlocal t19 = {[138311]=true,[138314]=true,[138317]=true,[138320]=true,[138323]=true,[138373]=true}\nlocal t19count = 0\nlocal t19slots = {1,3,5,7,10,15}\nfor _,slot in ipairs(t19slots) do if t19[GetInventoryItemID(\"player\",slot)] then t19count=t19count+1 end end\n\naura_env.demons = {\n    -- temporary pets\n    [196273] = { healthRatio = 0.15, lifespan = t19count > 3 and 16 or 12, units = {} },--wildimp 1: improved dreadstalkers\n    [196274] = { healthRatio = 0.15, lifespan = t19count > 3 and 16 or 12, units = {} },--wildimp 2: improved dreadstalkers\n    [104317] = { healthRatio = 0.15, lifespan = 12, units = {} },--wildimp 1-4: hog\n    [196271] = { healthRatio = 0.15, lifespan = 12, units = {} },--wildimp: impending doom\n    [193331] = { healthRatio = 0.4, lifespan = t19count > 3 and 16 or 12, units = {} },--dreadstalker1\n    [193332] = { healthRatio = 0.4, lifespan = t19count > 3 and 16 or 12, units = {} },--dreadstalker2\n    [205180] = { healthRatio = 0.4, lifespan = 12, units = {} },--darkglare\n    [60478] = { healthRatio = 0.4, lifespan = 25, units = {} },--doomguard\n    [000000] = { healthRatio = 0.4, lifespan = 25, units = {} },--terrorguard NYI?\n    [1122] = { healthRatio = 0.5, lifespan = 25, units = {} },--infernal\n    [000000] = { healthRatio = 0.5, lifespan = 25, units = {} },--abyssal NYI?\n    [111859] = { healthRatio = 0.3, lifespan = 25, units = {} },--g:imp\n    [000000] = { healthRatio = 0.3, lifespan = 25, units = {} },--g:fel imp NYI?\n    [111895] = { healthRatio = 0.5, lifespan = 25, units = {} },--g:voidwalker\n    [000000] = { healthRatio = 0.5, lifespan = 25, units = {} },--g:voidlord NYI?\n    [111897] = { healthRatio = 0.4, lifespan = 25, units = {} },--g:felhunter\n    [000000] = { healthRatio = 0.4, lifespan = 25, units = {} },--g:observer NYI?\n    [111896] = { healthRatio = 0.4, lifespan = 25, units = {} },--g:succubus\n    [000000] = { healthRatio = 0.4, lifespan = 25, units = {} },--g:shivarra NYI?\n    [111898] = { healthRatio = 0.5, lifespan = 25, units = {} },--g:felguard\n    [000000] = { healthRatio = 0.5, lifespan = 25, units = {} },--g:wrathguard NYI?\n}\n\naura_env.pguid = UnitGUID(\"player\")\naura_env.count = 0\naura_env.buff_gds = {time = 0}\naura_env.buff_sh = {time = 0}\naura_env.buff_ss = {time = 0}\naura_env.buff_wl = 0\n\naura_env.WakenerCount = function()\n    for i = 1, 40 do\n        local _,_,_,count,_,_,_,_,_,_,id = UnitBuff(\"player\", i)\n        if id == 236200 then\n            aura_env.buff_wl = count and count > 0 and count or 0\n            return\n        end\n        aura_env.buff_wl = 0\n    end\nend\n\n\naura_env.AddDemon = function(demonName,destGUID)\n    if aura_env.demons[demonName] then\n        aura_env.demons[demonName].units[destGUID] = {}\n        aura_env.demons[demonName].units[destGUID].dob = GetTime()\n        --print(\"added\",demonName)\n    end\nend\n\naura_env.RemoveDemon = function(destGUID)\n    for type, demon in pairs(aura_env.demons) do\n        for id,_ in pairs(demon.units) do\n            if id == destGUID then\n                demon.units[destGUID] = nil\n                --print(\"removed\",type)\n            end\n        end\n    end\nend\n\naura_env.DeSet = function(destGUID,deUp)\n    for type, demon in pairs(aura_env.demons) do\n        for id,_ in pairs(demon.units) do\n            if id == destGUID then\n                demon.units[destGUID].de = deUp\n                --print(\"de\",demon.units[destGUID].de, type)\n            end\n        end\n    end\nend\n\naura_env.FindDemonHealth = function(demon)\n    local playerHealth = UnitHealthMax(\"player\")\n    local demonHealth = demon.healthRatio * playerHealth\n    return demonHealth\nend\n\naura_env.CountDemonsPerType = function(demon)--not used\n    local count = 0\n    for k,v in pairs(demon.units) do\n        count = count + 1\n    end\n    return count\nend\n\naura_env.TotalDemonHealth = function() --also counts all pets\n    local totalDemonHealth = 0\n    local countPerType = 0\n    local countAll = 0\n    local deVal = aura_env.DemonicEmpowerment()\n    local playerHealth = UnitHealthMax(\"player\")\n    \n    for spellID, demon in pairs(aura_env.demons) do\n        countPerType = aura_env.CountDemonsPerType(demon)\n        \n        for unit, detail in pairs(demon.units) do\n            local de = detail.de and deVal or 1\n            totalDemonHealth = totalDemonHealth + (demon.healthRatio * playerHealth * de)\n        end\n        \n        countAll = countAll + countPerType\n    end\n    \n    if UnitExists(\"pet\") then\n        local petHPMax = UnitHealthMax(\"pet\")\n        --if UnitBuff(\"pet\",GetSpellInfo(193396)) then\n        --    petHPMax = petHPMax/aura_env.DemonicEmpowerment()\n        --end\n        totalDemonHealth = totalDemonHealth + petHPMax\n        countAll = countAll + 1\n    end\n    aura_env.count = countAll\n    return totalDemonHealth\nend\n\naura_env.FindArtifactTraitRank = function(spellID)\n    --https://wago.io/NkogH0_D- thanks Hamsda\n    local currentRank = 0\n    local loaded = IsAddOnLoaded(\"LibArtifactData-1.0\") or LoadAddOn(\"LibArtifactData-1.0\")\n    if loaded then\n        aura_env.LAD = aura_env.LAD or LibStub(\"LibArtifactData-1.0\")\n        if not aura_env.LAD:GetActiveArtifactID() then\n            aura_env.LAD:ForceUpdate()            \n        end\n        local _, traits = aura_env.LAD:GetArtifactTraits()\n        if traits then\n            for _,v in ipairs(traits) do\n                if v.spellID == spellID then\n                    currentRank = v.currentRank\n                    break\n                end\n            end\n        end\n    end\n    return currentRank\nend\n\naura_env.DemonicEmpowerment = function()\n    local demonicEmpowerment = 0.20\n    --Summoner's Prowess\n    local traitRank = aura_env.FindArtifactTraitRank(211108)\n    demonicEmpowerment = demonicEmpowerment + (traitRank * 0.02)\n    return 1+demonicEmpowerment\nend\n\naura_env.LingeringPower = function()\n    local lingeringPower = 0\n    --Thal'Kiel's Lingering Power\n    local traitRank = aura_env.FindArtifactTraitRank(214935)\n    if traitRank > 0 then\n        lingeringPower = (traitRank-1) * 0.005\n        lingeringPower = lingeringPower + 0.05\n    end\n    return 1+lingeringPower\nend\n\naura_env.ShadowMultiplier = function()\n    local shadowMultiplier = 0\n    --Breath of Thal'kiel\n    local traitRank = aura_env.FindArtifactTraitRank(221882)\n    shadowMultiplier = shadowMultiplier + (traitRank * 0.05)\n    return 1+shadowMultiplier\nend\n\naura_env.Versatility = function()\n    local versatility = GetCombatRatingBonus(CR_VERSATILITY_DAMAGE_DONE) + GetVersatilityBonus(CR_VERSATILITY_DAMAGE_DONE)\n    return 1+(versatility/100)\nend\n\naura_env.TotalDamage = function()\n    --print(aura_env.TotalDemonHealth(),aura_env.DemonicEmpowerment(),aura_env.tkcHealthRatio, aura_env.ShadowMultiplier(),aura_env.LingeringPower(),aura_env.Versatility())\n    local totalBuffedHealth = aura_env.TotalDemonHealth()\n    local totalUsedHealth = totalBuffedHealth * aura_env.tkcHealthRatio\n    local totalDamage = totalUsedHealth * aura_env.ShadowMultiplier() * aura_env.LingeringPower() * aura_env.Versatility()\n    return totalDamage\nend",
