		["Druid-Guardian Frenzied Regen v4.4"] = {
			["outline"] = true,
			["text2Point"] = "CENTER",
			["text1FontSize"] = 18,
			["xOffset"] = 127,
			["displayText"] = "%c",
			["customText"] = "function(...)\n    local i = 1\n    local cur = GetTime()\n    local total = 0\n    \n    --get total damage taken\n    while aura_env.table[i] do\n        --grab table entries with time and damage\n        local time = aura_env.table[i][1]\n        local dmg = aura_env.table[i][2]\n        \n        --if the damage happened outside of the timewindow\n        if cur > time + aura_env.lastSec then\n            --remove the entry\n            table.remove(aura_env.table, i)\n        else --otherwise\n            --add up the damage and go to next entry\n            total = total + dmg\n            i = i + 1\n        end\n    end\n    \n    --Mastery\n    local mastery = 1 + GetMastery() / 200\n    \n    --Versatility\n    local vers = 1 + ((GetCombatRatingBonus(29) + GetVersatilityBonus(30)) / 100)\n    \n    --Leech\n    local leech = 1 + GetLifesteal() / 100\n    \n    --Guardian of Elune\n    local goe = ((select(4, GetTalentInfo(6,2,1))) and UnitBuff(\"player\", GetSpellInfo(213680))) and 1.2 or 1\n    \n    --check artifact traits\n    local currentRank = 0\n    local loaded = IsAddOnLoaded(\"LibArtifactData-1.0\") or LoadAddOn(\"LibArtifactData-1.0\")\n    if loaded then\n        aura_env.LAD = aura_env.LAD or LibStub(\"LibArtifactData-1.0\")\n        if not aura_env.LAD:GetActiveArtifactID() then\n            aura_env.LAD:ForceUpdate()            \n        end\n        local _, traits = aura_env.LAD:GetArtifactTraits()\n        if traits then\n            for _,v in ipairs(traits) do\n                if v.spellID == 200400 then\n                    currentRank = v.currentRank\n                    break\n                end\n            end\n        end\n    end\n    local trait = 1 + 0.05 * currentRank\n    \n    --Guardian Spirit\n    local gs = UnitBuff(\"player\", GetSpellInfo(47788)) and 1.4 or 1\n    \n    --Divine Hymn\n    local dh = UnitBuff(\"player\", GetSpellInfo(64844)) and 1.1 or 1\n    \n    --Life Cocoon\n    local lc = UnitBuff(\"player\", GetSpellInfo(116849)) and 1.5 or 1\n    \n    --T17\n    local t17 = 1\n    local t17n, _, _, t17s = UnitBuff(\"player\", GetSpellInfo(177969))\n    if t17n then\n        t17 = 1 + t17s * 0.1\n    end\n    \n    --T18\n    local t18 = UnitBuff(\"player\", GetSpellInfo(192081)) and aura_env.GetNumSetPieces(\"T18\") >= 2 and 1.2 or 1\n    \n    --T19\n    local t19 = 1\n    local t19n, _, _, t19s = UnitBuff(\"player\", GetSpellInfo(211160))\n    if t19n then\n        t19 = 1 + (t19s / 3)\n    end\n    \n    --Skysec's Hold\n    local leg = IsEquippedItem(137025) and 0.100078 or 0 --restores ~10% of mastery affected maxhp\n    if leg > 0 then\n        --but the buff doesn't roll over like FR, so loses value if uses twice in quick succession\n        local exp = select(7, UnitBuff(\"player\", GetSpellInfo(208218))) or GetTime()\n        local rem = exp - GetTime()\n        if rem > 2 then\n            leg = leg * 1 / 3\n        elseif rem > 1 then\n            leg = leg * 2 / 3\n        end\n    end\n    \n    \n    local heal = total * aura_env.percDmg --damage taken * FR percentage\n    local perc = heal / UnitHealthMax(\"player\") --relative to maxHP\n    perc = math.max(aura_env.minHealthPerc, perc) --minimum FR percentage\n    perc = (perc * goe * trait * t17 * t18 * t19 * vers + leg) * mastery * leech * gs * dh * lc --apply all multipliers\n    heal = perc * UnitHealthMax(\"player\") --get the actual heal value\n    \n    if aura_env.showAbsolute then\n        return aura_env.shortenNumber(heal)..\"\\n\"\n    end\n    return string.format(\"%.1f%%%%\\n\", perc*100)\nend",
			["untrigger"] = {
				["custom"] = "function()\n    return false\nend\n\n\n\n\n\n",
			},
			["anchorPoint"] = "CENTER",
			["activeTriggerMode"] = 1,
