				["custom"] = "function(_,_,type,_,sguid,_,_,_,_,name,_,_,spellID,spell)\n    if sguid == aura_env.pguid then\n        if type == \"SPELL_SUMMON\" then\n            if spellID == 104317 or spellID == 196271 then\n                aura_env.Build(\"wi\")\n                return true\n            end\n            if spellID == 196273 or spellID == 196274 then\n                aura_env.Build(\"wi\", aura_env.t19count > 3 and 4) --extra duration for imps on dogs with 4pt19\n                return true\n            end\n            \n            if spellID == 193331 or spellID == 193332 then\n                aura_env.Build(\"ds\")\n                return true\n            end\n        end\n        if spellID == 193396 then\n            if type == \"SPELL_CAST_SUCCESS\" then\n                aura_env.DeIcons(\"dgl\", true)\n                aura_env.DeIcons(\"gr\", true)\n                aura_env.DeIcons(\"dg\", true)\n                aura_env.DeBars(\"wi\", true)\n                aura_env.DeBars(\"ds\", true)\n                return true\n            end\n            if type == \"SPELL_AURA_REMOVED\" then\n                aura_env.DeIcons(\"dgl\", false)\n                aura_env.DeIcons(\"gr\", false)\n                aura_env.DeIcons(\"dg\", false)\n                aura_env.DeBars(\"wi\", false)\n                aura_env.DeBars(\"ds\", false)\n                return true\n            end\n        end\n        if type == \"SPELL_INSTAKILL\" then\n            if spellID == 196278 then\n                aura_env.RemoveBar(\"wi\")\n                return true\n            end\n        end\n    end\nend",
