				["init"] = {
					["do_custom"] = true,
					["custom"] = "-- aura naming\nlocal prefix = \"NL: \"\nlocal wi = \"Wild Imp\"\nlocal ds = \"Dreadstalker\"\nlocal dgl = \"Darkglare\"\nlocal dg = \"Doomguard\"\nlocal gr = \"Grimoire of Service\"\nlocal t19 = {}\nlocal t19 = {[138311]=true,[138314]=true,[138317]=true,[138320]=true,[138323]=true,[138373]=true}\naura_env.t19count = 0\nlocal t19slots = {1,3,5,7,10,15}\nfor _,slot in ipairs(t19slots) do if t19[GetInventoryItemID(\"player\",slot)] then aura_env.t19count=aura_env.t19count+1 end end        \naura_env.pguid = UnitGUID(\"player\")\naura_env.wi = {}\naura_env.wi.data = WeakAuras.GetData(prefix..wi)\naura_env.wi.name = wi\naura_env.wi.shortname = \"OL\"..wi:gsub('%W',''):lower()\naura_env.wi.duration = 12\naura_env.wi.durationMax = aura_env.t19count > 3 and 16 or 12\naura_env.wi.count = 0\naura_env.ds = {}\naura_env.ds.data = WeakAuras.GetData(prefix..ds)\naura_env.ds.duration = aura_env.t19count > 3 and 16 or 12\naura_env.ds.durationMax = aura_env.t19count > 3 and 16 or 12\naura_env.ds.count = 0\naura_env.ds.name = ds\naura_env.ds.shortname = \"OL\"..ds:gsub('%W',''):lower()\nlocal de = \"Demonic Empowerment\"\naura_env.de = {}\naura_env.de.data = WeakAuras.GetData(prefix..de)\naura_env.de.name = de\naura_env.de.shortname = \"OL\"..de:gsub('%W',''):lower()\naura_env.dgl = {}\naura_env.dgl.name = dgl\naura_env.dg = {}\naura_env.dg.name = dg\naura_env.gr = {}\naura_env.gr.name = gr\n\naura_env.Build = function(demon,t19extra)\n    aura_env[demon].count = aura_env[demon].count+1\n    local count = aura_env[demon].count\n    local extra = t19extra or 0\n    \n    local duration = aura_env[demon].duration+extra\n    local durationMax = aura_env[demon].durationMax\n    --print(extra,duration,durationMax)\n    local barName = aura_env[demon].shortname..\"Bar\"..count\n    if not _G[barName] then\n        _G[barName] = CreateFrame(\"StatusBar\",barName,WeakAuras.regions[aura_env.id].region)\n        local bar = _G[barName]\n        bar.name = barName\n        bar:SetHeight(aura_env[demon].data.height)\n        bar:SetWidth(aura_env[demon].data.width)\n        bar:SetPoint(\"TOPLEFT\",WeakAuras.regions[prefix..aura_env[demon].name].region,\"TOPLEFT\",0,0)\n        bar:SetFrameStrata(\"MEDIUM\")\n        \n        bar:SetStatusBarTexture(\"Interface/Buttons/WHITE8X8\")\n        bar.texture=bar:GetStatusBarTexture()\n        bar:SetBackdrop({bgFile = \"Interface/Tooltips/UI-Tooltip-Background\"})\n        bar:SetBackdropColor(0,0,0,0)\n        bar.duration = duration\n        bar:SetMinMaxValues(0, durationMax)\n        local spark = bar:CreateTexture(nil, \"ARTWORK\")\n        spark:SetDrawLayer(\"ARTWORK\", 3)\n        bar.spark = spark\n        bar.spark:SetTexture(aura_env[demon].data.sparkTexture)\n        bar.spark:SetWidth(aura_env[demon].data.sparkWidth)\n        bar.spark:SetHeight(aura_env[demon].data.sparkHeight)\n        bar.spark:SetPoint(\"CENTER\", bar, \"LEFT\", aura_env[demon].data.width, 0)\n        bar.spark:SetBlendMode(aura_env[demon].data.sparkBlendMode)\n        if count == 1 then\n            local bg = _G[barName]:CreateTexture(nil, \"LOW\", nil, -8)\n            bar.bg = bg\n            bar.bg:SetTexture(\"Interface/Buttons/WHITE8X8\")\n            local bgc = aura_env[demon].data.backgroundColor\n            bar.bg:SetVertexColor(bgc[1],bgc[2],bgc[3],bgc[4])\n            bar.bg:SetHeight(aura_env[demon].data.height)\n            bar.bg:SetWidth(aura_env[demon].data.width)\n            bar.bg:SetPoint(\"CENTER\", bar, \"CENTER\", 0, 0)\n        end\n    end\n    local bar = _G[barName]\n    -- these need to be set again on each Imp spawn\n    bar:SetFrameLevel(count>1000 and 0 or 1000-count)\n    bar.level = bar:GetFrameLevel()\n    bar.deUp = false\n    bar.expires = GetTime()+duration\n    bar:SetValue(duration)\n    local bc = aura_env[demon].data.barColor\n    aura_env.SetGradient(bar,bc,\"build----\")\n    bar.spark:SetPoint(\"CENTER\", bar, \"LEFT\", aura_env[demon].data.width, 0)\n    local sparkColor = aura_env[demon].data.sparkColor\n    bar.spark:SetVertexColor(sparkColor[1], sparkColor[2], sparkColor[3], sparkColor[4])\n    bar:Show()\nend\naura_env.SetGradient = function(bar,bc,...)\n    --print(string.format(\"\\124cFF%02x%02x%02x\", bc[1]*255, bc[2]*255, bc[3]*255),bar.name,...)\n    bar.texture:SetGradientAlpha(\"HORIZONTAL\",bc[1]*0.9,bc[2]*0.9,bc[3]*0.9,0.9,bc[1]*1.2,bc[2]*1.2,bc[3]*1.2,1)\nend\n\naura_env.DeIcons = function(demon,deUp)\n    local id = prefix..aura_env[demon].name\n    if WeakAuras.regions[id] and WeakAuras.regions[id].region and WeakAuras.regions[id].region.icon then \n        if deUp then\n            WeakAuras.regions[id].region.stacks:SetVertexColor(0,1,0,1) --de up color\n            -- WeakAuras.regions[id].region.icon:SetBlendMode(\"ADD\")\n            WeakAuras.regions[id].region.icon:SetVertexColor(1,1,1,1)\n        else \n            if UnitExists(\"pet\") and not UnitAura(\"pet\",GetSpellInfo(193396)) then\n                WeakAuras.regions[id].region.stacks:SetVertexColor(1,0,0,1) --missing de color\n                --WeakAuras.regions[id].region.icon:SetBlendMode(\"BLEND\")\n                WeakAuras.regions[id].region.icon:SetVertexColor(1,1,1,1)\n            end\n        end\n    end\nend\n\naura_env.DeBars = function(demon,deUp)\n    local id = prefix..aura_env[demon].name\n    if WeakAuras.regions[id] and WeakAuras.regions[id].region and WeakAuras.regions[id].region.bar then\n        local count = aura_env[demon].count\n        if deUp then\n            local bc = aura_env.de.data.barColor\n            for i=1,count,1 do\n                aura_env.SetGradient(_G[aura_env[demon].shortname..\"Bar\"..i],bc,i,\"deUp\",deUp)\n                _G[aura_env[demon].shortname..\"Bar\"..i].deUp = true\n            end\n        else\n            if UnitExists(\"pet\") and not UnitAura(\"pet\",GetSpellInfo(193396)) then\n                local bc = aura_env[demon].data.barColor\n                for i=1,count,1 do\n                    aura_env.SetGradient(_G[aura_env[demon].shortname..\"Bar\"..i],bc,i,\"deUp\",deUp)\n                    _G[aura_env[demon].shortname..\"Bar\"..i].deUp = false\n                end\n            end\n        end\n    end\nend\n\naura_env.RemoveBar = function(demon)\n    local count = aura_env[demon].count\n    for i=count,1,-1 do\n        _G[aura_env[demon].shortname..\"Bar\"..count].expires=GetTime()\n        local bc = aura_env[demon].data.barColor\n        aura_env.SetGradient(_G[aura_env[demon].shortname..\"Bar\"..count],bc,\"remove\")\n    end\nend\n\naura_env.Update = function(demon)\n    local count = aura_env[demon].count or 0\n    if count > 0 then\n        for i=1,count,1 do\n            local bar = _G[aura_env[demon].shortname..\"Bar\"..i]\n            if bar then\n                local expires = bar.expires\n                local countdown = expires-GetTime()\n                if expires and countdown > 0 then\n                    bar:SetValue(countdown)\n                    bar.spark:ClearAllPoints()\n                    local x = (countdown/aura_env[demon].durationMax)*aura_env[demon].data.width\n                    bar.spark:SetPoint(\"CENTER\", bar, \"LEFT\", x, 0);\n                    --if countdown < 0.019 then print(bar.name,\"end\") end\n                else\n                    for j=count-1,1,-1 do\n                        \n                        local nextExpires = _G[aura_env[demon].shortname..\"Bar\"..(count-j+1)].expires\n                        _G[aura_env[demon].shortname..\"Bar\"..(count-j)].expires = nextExpires\n                        local nextDeUp = _G[aura_env[demon].shortname..\"Bar\"..(j+1)].deUp\n                        _G[aura_env[demon].shortname..\"Bar\"..(j)].deUp = nextDeUp\n                        local newFrameLevel =  _G[aura_env[demon].shortname..\"Bar\"..(j)]:GetFrameLevel()\n                        _G[aura_env[demon].shortname..\"Bar\"..(j)]:SetFrameLevel(newFrameLevel)\n                        local bc = {}\n                        local deUp = _G[aura_env[demon].shortname..\"Bar\"..(j)].deUp\n                        if deUp then\n                            bc = aura_env.de.data.barColor\n                        else\n                            bc = aura_env[demon].data.barColor\n                        end\n                        \n                        aura_env.SetGradient(_G[aura_env[demon].shortname..\"Bar\"..(j)],bc,\"update\",j+1,\"deUp\",deUp,nextDeUp)\n                    end\n                    _G[aura_env[demon].shortname..\"Bar\"..(count)]:Hide()\n                    aura_env[demon].count = count -1\n                    \n                end\n            end\n        end\n    end\n    _G[\"WA_OOMM_\"..demon:upper()..\"_COUNT\"] = aura_env[demon].count\nend",
				},
